import tailwindcss from '@tailwindcss/vite'
import react from '@vitejs/plugin-react'
import { defineConfig } from 'vite'

export default defineConfig({
  plugins: [react(), tailwindcss()],
  server: {
    port: 4000,
    strictPort: true,
  },
})

@import url('https://fonts.googleapis.com/css2?family=Bebas+Neue&family=Chivo:wght@400;500;700;900&family=Fraunces:opsz,wght@9..144,500;9..144,700;9..144,900&family=Instrument+Serif:ital@0;1&family=JetBrains+Mono:wght@400;600;700&family=Plus+Jakarta+Sans:wght@400;500;600;700;800&family=Public+Sans:wght@400;500;600;700&family=Red+Hat+Display:wght@400;500;700;900&family=Syne:wght@400;500;600;700;800&display=swap');
@import 'tailwindcss';

:root {
  font-synthesis: none;
  text-rendering: optimizeLegibility;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

* {
  box-sizing: border-box;
}

html,
body,
#root {
  min-height: 100%;
}

body {
  margin: 0;
  min-width: 320px;
  background: #05080f;
}

a,
button,
input,
textarea {
  -webkit-tap-highlight-color: transparent;
}

button,
input,
textarea,
select {
  font: inherit;
}

:focus-visible {
  outline: 2px solid #f8fafc;
  outline-offset: 2px;
}

::selection {
  background: rgba(72, 181, 255, 0.38);
}

.font-syne {
  font-family: 'Syne', sans-serif;
}

.font-jakarta {
  font-family: 'Plus Jakarta Sans', sans-serif;
}

.font-fraunces {
  font-family: 'Fraunces', serif;
}

.font-public {
  font-family: 'Public Sans', sans-serif;
}

.font-bebas {
  font-family: 'Bebas Neue', sans-serif;
}

.font-redhat {
  font-family: 'Red Hat Display', sans-serif;
}

.font-chivo {
  font-family: 'Chivo', sans-serif;
}

.font-mono {
  font-family: 'JetBrains Mono', monospace;
}

.font-instrument {
  font-family: 'Instrument Serif', serif;
}

.text-balance {
  text-wrap: balance;
}

@keyframes float-soft {
  0%,
  100% {
    transform: translateY(0px);
  }

  50% {
    transform: translateY(-12px);
  }
}

@keyframes beam-pan {
  0% {
    transform: translateX(-16%) rotate(0deg);
  }

  100% {
    transform: translateX(16%) rotate(2deg);
  }
}

@keyframes marquee {
  0% {
    transform: translateX(0);
  }

  100% {
    transform: translateX(-50%);
  }
}

@keyframes grid-drift {
  0% {
    transform: translateY(0);
  }

  100% {
    transform: translateY(36px);
  }
}

@keyframes rise {
  0% {
    opacity: 0;
    transform: translateY(18px);
  }

  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes pulse-dot {
  0%,
  100% {
    opacity: 0.3;
    transform: scale(1);
  }

  50% {
    opacity: 1;
    transform: scale(1.2);
  }
}

.animate-float-soft {
  animation: float-soft 7s ease-in-out infinite;
}

.animate-beam-pan {
  animation: beam-pan 11s ease-in-out infinite alternate;
}

.animate-marquee {
  display: inline-block;
  min-width: 200%;
  animation: marquee 19s linear infinite;
}

.animate-grid-drift {
  animation: grid-drift 14s linear infinite;
}

.animate-rise {
  animation: rise 480ms cubic-bezier(0.2, 0.7, 0.2, 1) both;
}

.animate-pulse-dot {
  animation: pulse-dot 2.1s ease-in-out infinite;
}

input[type='range']::-webkit-slider-thumb {
  appearance: none;
  height: 18px;
  width: 18px;
  border-radius: 999px;
  border: 2px solid #f8fafc;
  background: #0f172a;
}

input[type='range']::-moz-range-thumb {
  height: 18px;
  width: 18px;
  border: 2px solid #f8fafc;
  border-radius: 999px;
  background: #0f172a;
}

@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }
}

import { NavLink } from 'react-router-dom'

const concepts = [
  { id: '1', name: 'Pulse Forge' },
  { id: '2', name: 'Editorial Core' },
  { id: '3', name: 'Command Stack' },
  { id: '4', name: 'Gallery Arc' },
  { id: '5', name: 'Poster Storm' },
]

export function ConceptNav() {
  return (
    <nav className="fixed left-1/2 top-3 z-50 w-[calc(100%-1rem)] max-w-4xl -translate-x-1/2 rounded-2xl border border-white/20 bg-black/35 p-1.5 shadow-[0_10px_38px_rgba(0,0,0,0.4)] backdrop-blur-xl md:top-4 md:w-[calc(100%-2.4rem)]">
      <div className="grid grid-cols-5 gap-1">
        {concepts.map((concept) => (
          <NavLink
            key={concept.id}
            to={`/${concept.id}`}
            className={({ isActive }) =>
              [
                'rounded-xl px-1.5 py-2 text-center text-[10px] font-semibold tracking-[0.12em] transition md:px-2 md:text-xs',
                isActive
                  ? 'bg-white text-black shadow-sm'
                  : 'text-white/80 hover:bg-white/10 hover:text-white',
              ].join(' ')
            }
          >
            {concept.name}
          </NavLink>
        ))}
      </div>
    </nav>
  )
}
