---
import { StrictMode } from 'react'
import { createRoot } from 'react-dom/client'
import { BrowserRouter } from 'react-router-dom'
import './index.css'
import App from './App.tsx'

createRoot(document.getElementById('root')!).render(
  <StrictMode>
    <BrowserRouter>
      <App />
    </BrowserRouter>
  </StrictMode>,
)
---
import { Navigate, Route, Routes } from 'react-router-dom'
import { HomePage } from './pages/HomePage'
import { RouteOne } from './pages/RouteOne'
import { RouteTwo } from './pages/RouteTwo'
import { RouteThree } from './pages/RouteThree'
import { RouteFour } from './pages/RouteFour'
import { RouteFive } from './pages/RouteFive'

function App() {
  return (
    <Routes>
      <Route path="/" element={<HomePage />} />
      <Route path="/1" element={<RouteOne />} />
      <Route path="/2" element={<RouteTwo />} />
      <Route path="/3" element={<RouteThree />} />
      <Route path="/4" element={<RouteFour />} />
      <Route path="/5" element={<RouteFive />} />
      <Route path="*" element={<Navigate to="/" replace />} />
    </Routes>
  )
}

export default App
---
@import url('https://fonts.googleapis.com/css2?family=Bebas+Neue&family=Chivo:wght@400;600;700;900&family=DM+Serif+Display:ital@0;1&family=Fraunces:opsz,wght@9..144,300..900&family=IBM+Plex+Sans:wght@400;500;600;700&family=JetBrains+Mono:wght@400;600;700&family=Manrope:wght@400;500;600;700;800&family=Outfit:wght@400;500;600;700;800&family=Sora:wght@400;600;700;800&display=swap');
@import 'tailwindcss';

:root {
  font-family: 'Manrope', sans-serif;
  line-height: 1.5;
  font-weight: 400;
  color: #f0f3ff;
  background: #08090f;
  font-synthesis: none;
  text-rendering: optimizeLegibility;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

html,
body,
#root {
  min-height: 100%;
}

body {
  margin: 0;
  min-width: 320px;
  overflow-x: hidden;
}

a {
  color: inherit;
  text-decoration: none;
}

button {
  font: inherit;
}

.font-display-1 {
  font-family: 'Sora', sans-serif;
}

.font-body-1 {
  font-family: 'IBM Plex Sans', sans-serif;
}

.font-display-2 {
  font-family: 'DM Serif Display', serif;
}

.font-body-2 {
  font-family: 'Manrope', sans-serif;
}

.font-display-3 {
  font-family: 'Chivo', sans-serif;
}

.font-body-3 {
  font-family: 'JetBrains Mono', monospace;
}

.font-display-4 {
  font-family: 'Fraunces', serif;
}

.font-body-4 {
  font-family: 'Outfit', sans-serif;
}

.font-display-5 {
  font-family: 'Bebas Neue', sans-serif;
}

.font-body-5 {
  font-family: 'Chivo', sans-serif;
}

.grain-bg {
  background-image: radial-gradient(circle at 1px 1px, rgb(255 255 255 / 0.06) 1px, transparent 0);
  background-size: 22px 22px;
}

.poster-lines {
  background-image: linear-gradient(120deg, rgb(255 255 255 / 0.08) 0 2px, transparent 2px 24px);
  background-size: 28px 28px;
}

@keyframes float-y {
  0%,
  100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-10px);
  }
}

@keyframes pulse-outline {
  0% {
    box-shadow: 0 0 0 0 rgb(251 191 36 / 0.42);
  }
  100% {
    box-shadow: 0 0 0 14px rgb(251 191 36 / 0);
  }
}

@keyframes slide-marquee {
  from {
    transform: translateX(0);
  }
  to {
    transform: translateX(-50%);
  }
}

@keyframes fade-up {
  from {
    opacity: 0;
    transform: translateY(18px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes kinetic-shift {
  0%,
  100% {
    transform: translate3d(0, 0, 0) scale(1);
    letter-spacing: 0.01em;
  }
  50% {
    transform: translate3d(6px, -4px, 0) scale(1.02);
    letter-spacing: 0.045em;
  }
}

.motion-safe-float {
  animation: float-y 6.5s ease-in-out infinite;
}

.motion-safe-pulse-outline {
  animation: pulse-outline 2s ease-out infinite;
}

.motion-safe-marquee {
  animation: slide-marquee 14s linear infinite;
}

.motion-safe-fade-up {
  animation: fade-up 680ms ease-out both;
}

.motion-safe-kinetic {
  animation: kinetic-shift 4.2s ease-in-out infinite;
}

@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }
}
