/usr/bin/zsh -lc "cd t4-canvas && cat > src/index.css <<'EOF'
@import url('https://fonts.googleapis.com/css2?family=Anton&family=Archivo+Black&family=Azeret+Mono:wght@400;500;700&family=Bricolage+Grotesque:opsz,wght@12..96,300..800&family=Cormorant+Garamond:wght@400;500;600;700&family=DM+Sans:wght@400;500;700&family=Fraunces:opsz,wght@9..144,200..900&family=IBM+Plex+Sans:wght@400;500;700&family=Manrope:wght@500;700;800&family=Syne:wght@400;500;700;800&display=swap');
@import \"tailwindcss\";

:root {
  --font-ui: \"DM Sans\", sans-serif;
  --font-display: \"Bricolage Grotesque\", sans-serif;
  --font-serif: \"Fraunces\", serif;
  --font-mono: \"Azeret Mono\", monospace;
  --font-heavy: \"Archivo Black\", sans-serif;
  --font-noir: \"Syne\", sans-serif;
  --font-editorial: \"Cormorant Garamond\", serif;
  --grain-opacity: 0.07;

  text-rendering: optimizeLegibility;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

*,
*::before,
*::after {
  box-sizing: border-box;
}

body {
  margin: 0;
  min-width: 320px;
  font-family: var(--font-ui);
  background: #0b0d10;
  color: #eef0f6;
}

a {
  color: inherit;
  text-decoration: none;
}

#root {
  min-height: 100vh;
}

.font-display {
  font-family: var(--font-display);
}

.font-serif {
  font-family: var(--font-serif);
}

.font-mono {
  font-family: var(--font-mono);
}

.font-heavy {
  font-family: var(--font-heavy);
}

.font-noir {
  font-family: var(--font-noir);
}

.font-editorial {
  font-family: var(--font-editorial);
}

.surface-grain::before {
  pointer-events: none;
  position: absolute;
  inset: 0;
  content: \"\";
  opacity: var(--grain-opacity);
  background-image:
    radial-gradient(circle at 20% 15%, rgba(255, 255, 255, 0.6) 0.4px, transparent 0.6px),
    radial-gradient(circle at 80% 70%, rgba(255, 255, 255, 0.6) 0.4px, transparent 0.6px);
  background-size: 3px 3px, 4px 4px;
}

@keyframes slow-float {
  0%,
  100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
}

@keyframes pulse-ring {
  0% {
    transform: scale(0.95);
    opacity: 0.7;
  }
  70% {
    transform: scale(1);
    opacity: 1;
  }
  100% {
    transform: scale(0.95);
    opacity: 0.7;
  }
}

.animate-slow-float {
  animation: slow-float 8s ease-in-out infinite;
}

.animate-pulse-ring {
  animation: pulse-ring 3.8s ease-in-out infinite;
}

.no-scrollbar {
  -ms-overflow-style: none;
  scrollbar-width: none;
}

.no-scrollbar::-webkit-scrollbar {
  display: none;
}

.section-shell {
  position: relative;
  overflow: clip;
  border-radius: 1.5rem;
}
EOF"

