@import url('https://fonts.googleapis.com/css2?family=Baloo+2:wght@500;700;800&family=Bebas+Neue&family=Cabin:wght@400;500;700&family=Cormorant+Garamond:wght@400;500;600;700&family=Fraunces:opsz,wght@9..144,400;9..144,500;9..144,700&family=IBM+Plex+Mono:wght@400;500;700&family=Orbitron:wght@500;700;800&family=Plus+Jakarta+Sans:wght@400;500;700;800&family=Source+Sans+3:wght@400;500;600;700&family=Sora:wght@400;500;600;700&display=swap');
@import 'tailwindcss';

:root {
  --ink-050: #f8fafc;
  --ink-100: #e2e8f0;
  --ink-300: #94a3b8;
  --ink-800: #1e293b;
  --ink-900: #0f172a;
  --ink-950: #020617;
}

* {
  box-sizing: border-box;
}

html,
body,
#root {
  width: 100%;
  min-height: 100%;
}

body {
  margin: 0;
  font-family: 'Sora', sans-serif;
  background: var(--ink-950);
  color: var(--ink-050);
  text-rendering: geometricPrecision;
  overflow-x: hidden;
}

a {
  color: inherit;
  text-decoration: none;
}

::selection {
  background: rgb(16 185 129 / 35%);
}

.font-orbitron {
  font-family: 'Orbitron', sans-serif;
}

.font-sora {
  font-family: 'Sora', sans-serif;
}

.font-fraunces {
  font-family: 'Fraunces', serif;
}

.font-source {
  font-family: 'Source Sans 3', sans-serif;
}

.font-baloo {
  font-family: 'Baloo 2', cursive;
}

.font-cabin {
  font-family: 'Cabin', sans-serif;
}

.font-bebas {
  font-family: 'Bebas Neue', sans-serif;
}

.font-ibm-mono {
  font-family: 'IBM Plex Mono', monospace;
}

.font-cormorant {
  font-family: 'Cormorant Garamond', serif;
}

.font-jakarta {
  font-family: 'Plus Jakarta Sans', sans-serif;
}

.noise-overlay::before {
  content: '';
  position: absolute;
  inset: 0;
  pointer-events: none;
  background-image: radial-gradient(rgb(255 255 255 / 7%) 0.6px, transparent 0.7px);
  background-size: 4px 4px;
  opacity: 0.15;
  mix-blend-mode: soft-light;
}

.mesh-fade {
  background-image: radial-gradient(circle at 20% 10%, rgb(20 184 166 / 45%), transparent 40%),
    radial-gradient(circle at 80% 10%, rgb(249 115 22 / 30%), transparent 35%),
    radial-gradient(circle at 50% 80%, rgb(14 165 233 / 20%), transparent 35%);
}

.glass-panel {
  background: rgb(15 23 42 / 58%);
  border: 1px solid rgb(148 163 184 / 22%);
  backdrop-filter: blur(16px);
}

.text-shadow-tight {
  text-shadow: 0 4px 20px rgb(0 0 0 / 30%);
}

@keyframes drift {
  0% {
    transform: translate3d(0, 0, 0) rotate(0deg);
  }
  50% {
    transform: translate3d(-2%, 3%, 0) rotate(4deg);
  }
  100% {
    transform: translate3d(0, 0, 0) rotate(0deg);
  }
}

@keyframes floaty {
  0% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-8px);
  }
  100% {
    transform: translateY(0);
  }
}

@keyframes scan {
  0% {
    transform: translateX(-100%);
  }
  100% {
    transform: translateX(250%);
  }
}

@keyframes marquee {
  0% {
    transform: translateX(0);
  }
  100% {
    transform: translateX(-50%);
  }
}

@keyframes pulseRing {
  0% {
    transform: scale(0.94);
    opacity: 0.22;
  }
  70% {
    transform: scale(1.03);
    opacity: 0.55;
  }
  100% {
    transform: scale(0.94);
    opacity: 0.22;
  }
}

.animate-drift {
  animation: drift 14s ease-in-out infinite;
}

.animate-floaty {
  animation: floaty 5.5s ease-in-out infinite;
}

.animate-scan {
  animation: scan 5s linear infinite;
}

.animate-marquee {
  animation: marquee 24s linear infinite;
}

.animate-pulse-ring {
  animation: pulseRing 3.6s ease-in-out infinite;
}

.section-wrap {
  margin-inline: auto;
  width: min(1120px, calc(100% - 2.5rem));
}

@media (max-width: 640px) {
  .section-wrap {
    width: min(1120px, calc(100% - 1.4rem));
  }
}
EOF"

