/usr/bin/zsh -lc "cd t4-canvas && cat > src/index.css <<'EOF'
@import url('https://fonts.googleapis.com/css2?family=Abril+Fatface&family=Archivo:wght@400;500;600;700;800&family=Chivo+Mono:wght@400;500;700&family=Manrope:wght@400;500;600;700;800&family=Syne:wght@400;500;700;800&family=Unbounded:wght@400;500;700;800&display=swap');
@import 'tailwindcss';

:root {
  font-synthesis: none;
  text-rendering: geometricPrecision;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

* {
  box-sizing: border-box;
}

html,
body,
#root {
  min-height: 100%;
}

body {
  margin: 0;
  overflow-x: hidden;
  font-family: 'Manrope', sans-serif;
  background: #06070b;
  color: #f8f8f8;
}

a {
  color: inherit;
  text-decoration: none;
}

button,
input,
select {
  font: inherit;
}

::selection {
  background: rgba(255, 225, 85, 0.4);
}

.font-syne {
  font-family: 'Syne', sans-serif;
}

.font-editorial {
  font-family: 'Abril Fatface', serif;
  letter-spacing: 0.01em;
}

.font-archivo {
  font-family: 'Archivo', sans-serif;
}

.font-mono-tech {
  font-family: 'Chivo Mono', monospace;
}

.font-unbounded {
  font-family: 'Unbounded', sans-serif;
}

.noise-overlay::before {
  content: '';
  position: absolute;
  inset: 0;
  pointer-events: none;
  background-image: radial-gradient(circle at 1px 1px, rgba(255, 255, 255, 0.09) 1px, transparent 0);
  background-size: 3px 3px;
  opacity: 0.2;
}

.paper-overlay::before {
  content: '';
  position: absolute;
  inset: 0;
  pointer-events: none;
  background-image: linear-gradient(160deg, rgba(0, 0, 0, 0.04), transparent),
    repeating-linear-gradient(0deg, rgba(0, 0, 0, 0.025), rgba(0, 0, 0, 0.025) 1px, transparent 1px, transparent 3px);
  opacity: 0.25;
}

.scanlines::before {
  content: '';
  position: absolute;
  inset: 0;
  pointer-events: none;
  background: repeating-linear-gradient(
    to bottom,
    rgba(255, 255, 255, 0.08),
    rgba(255, 255, 255, 0.08) 1px,
    transparent 1px,
    transparent 4px
  );
  opacity: 0.2;
}

@keyframes floatY {
  0%,
  100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-8px);
  }
}

@keyframes pulseRing {
  0% {
    transform: scale(0.94);
    opacity: 0.25;
  }
  100% {
    transform: scale(1.05);
    opacity: 0.7;
  }
}

@keyframes slideGlow {
  0% {
    transform: translateX(-18%);
  }
  100% {
    transform: translateX(18%);
  }
}

.animate-float {
  animation: floatY 4s ease-in-out infinite;
}

.animate-pulse-ring {
  animation: pulseRing 2.8s ease-in-out infinite alternate;
}

.animate-slide-glow {
  animation: slideGlow 3.5s ease-in-out infinite alternate;
}
EOF"

